function f = R_ca_L(cL,cs,t,T)% Release rate for L-type calcium channel% cs_tilde = 0.5; % M, The calcium inactivation thresholdgam = 1; % gamma value in the calcium dynamics paperd = (1+exp(-(AP(t,T) - 5)./(6.24))).^(-1); % instantaneous voltage dependent activation gateff = (1+exp((AP(t,T)+35)./(8.6))).^(-1); % slow voltage dependent inactivation gate variableq = (1 + (cs.*1e-6./cs_tilde).^gam).^(-1); % calcium-induced inactivationk_l = 50e-6;n_l = 7;f = d.*ff.*q.* f_h(cL,k_l,n_l) + (1- d.*ff.*q).* f_h(1e-4.*cs,k_l,n_l);end